    consecutive_spaces = 0

    for char in text:
        if char == " ":
            consecutive_spaces += 1
            if consecutive_spaces > 2:
                new_text += "-"
        else:
            if consecutive_spaces > 2:
                new_text += "-"
            elif consecutive_spaces > 0:
