def unique(l: list):
    """Transform your list with sorted, one-of-a-kind elements!
    >>> unique([5, 3, 5, 2, 3, 3, 9, 0, 123])
    [0, 2, 3, 5, 9, 123]
    """
    return sorted(set(l))

def encode_shift(s: str):
    """
    Encodes the input string by shifting each character forward by 5 in the alphabet.
    """
    return "".join([chr(((ord(ch) + 5 - ord("a")) % 26) + ord("a")) for ch in s])


def decode_shift(s: str):
    """
    Decodes a string encoded with the encode_shift function by shifting each character backward by 5 in the alphabet.
    """
    return "".join([chr(((ord(ch) - 5 - ord("a")) % 26) + ord("a")) for ch in s])

