def largest_prime_factor(n: int):
    """Compute and return the largest prime factor of the given integer n. Note: n must be greater than 1 and should not be a prime number.
    >>> largest_prime_factor(13195)
    29
    >>> largest_prime_factor(2048)
    2
    """
    def is_prime(k):
        if k < 2:
            return False
        for i in range(2, k):
            if k % i == 0:
                return False
        return True

    largest = 1
    for j in range(2, n + 1):
        if n % j == 0 and is_prime(j):
            largest = j

    return largest
