def is_multiply_prime(a):
    """
    Determines if the given number is the product of exactly three prime numbers.

    Args:
    a (int): The number to check, where 0 < a < 100.

    Returns:
    bool: True if `a` is the product of three prime numbers, False otherwise.

    Example:
    is_multiply_prime(30) == True  # Because 30 = 2 * 3 * 5
    """

    def is_prime(n):
        """Helper function to check if a number is prime."""
        if n < 2:
            return False
        for j in range(2, int(n**0.5) + 1):
            if n % j == 0:
                return False
        return True

    primes = [i for i in range(2, 101) if is_prime(i)]

    for i in primes:
        for j in primes:
            for k in primes:
                if i * j * k == a:
                    return True
    return False
