def extract_words(s):
    """
    Given a string containing words separated by commas or spaces, split it into individual words
    and return them as a list.

    Examples:
    extract_words("Hi, my name is John") == ["Hi", "my", "name", "is", "John"]
    extract_words("One, two, three, four, five, six") == ["One", "two", "three", "four", "five", "six"]
    """
    if not s:
        return []

    s_list = []

    for char in s:
        if char == ',':
            s_list.append(' ')
        else:
            s_list.append(char)

    s_list = "".join(s_list)
    return s_list.split()
