def histogram(test):
    """
    Given a string representing space-separated lowercase letters, this function returns a dictionary
    containing the letter(s) with the highest frequency and their corresponding counts. If multiple 
    letters have the same highest frequency, all such letters are included in the dictionary.

    Example:
    histogram('a b c') == {'a': 1, 'b': 1, 'c': 1}
    histogram('a b b a') == {'a': 2, 'b': 2}
    histogram('a b c a b') == {'a': 2, 'b': 2}
    histogram('b b b b a') == {'b': 4}
    histogram('') == {}
    """
    letter_counts = {}
    letter_list = test.split(" ")

    # Find the maximum frequency of occurrence
    max_count = 0
    for letter in letter_list:
        if letter:
            current_count = letter_list.count(letter)
            if current_count > max_count:
                max_count = current_count

    # Populate the dictionary with letters having the maximum frequency
    if max_count > 0:
        for letter in letter_list:
            if letter_list.count(letter) == max_count and letter not in letter_counts:
                letter_counts[letter] = max_count

    return letter_counts
