from typing import List

def unique_elements(numbers: List[int]) -> List[int]:
    """ Returns a list of integers with all duplicates removed, preserving the original order.
    >>> unique_elements([1, 2, 3, 2, 4])
    [1, 3, 4]
    """
    count_dict = {}
    for num in numbers:
        if num not in count_dict:
            count_dict[num] = 1
        else:
            count_dict[num] += 1
    return [num for num in numbers if count_dict[num] == 1]
