def histogram(test_string):
    """Given a string of space-separated lowercase letters, returns a dictionary containing 
    the letter(s) with the highest frequency of occurrence and their corresponding count.
    In the event of a tie, all letters with the maximum frequency are included.

    Examples:
    histogram('a b c') == {'a': 1, 'b': 1, 'c': 1}
    histogram('a b b a') == {'a': 2, 'b': 2}
    histogram('a b c a b') == {'a': 2, 'b': 2}
    histogram('b b b b a') == {'b': 4}
    histogram('') == {}

    """
    frequency_dict = {}
    letter_list = test_string.split(" ")
    max_frequency = 0

    for letter in letter_list:
        if letter_list.count(letter) > max_frequency and letter != '':
            max_frequency = letter_list.count(letter)
    if max_frequency > 0:
        for letter in letter_list:
            if letter_list.count(letter) == max_frequency:
                frequency_dict[letter] = max_frequency
    return frequency_dict
